// SPDX-License-Identifier: MIT
pragma solidity >=0.4.21 <0.7.0;

contract PinkyPromise {
    struct PinkyUserRecord {
        uint256 id;
        address addr;
        string name;
        // indicates whether only "known" users can create promises with this user as recipient
        bool allowOnlyKnownUserPromises;
    }

    struct PinkyPromiseRecord {
        uint256 id;
        string title;
        string description;
        uint256 createdAt;
        // the time before which both users must mark the promise as complete. after this time, neither user can update
        // the promise completion status
        uint256 deadline;
        // indicates whether the sharing user has marked the promise as complete
        bool completedBySharingUser;
        // indicates whether the receiving user has marked the promise as complete
        bool completedByReceivingUser;
        // the user who created the promise
        address sharingUser;
        // the user with who the promise must be shared
        address receivingUser;
        // indicates whether the promise should be visible only to the sharing and receiving users
        bool isPrivatePromise;
    }

    string public name;

    uint256 public usersCount = 0;
    mapping(uint256 => PinkyUserRecord) public users;
    mapping(address => uint256) public userIds;

    uint256 public promiseCount = 0;
    mapping(uint256 => PinkyPromiseRecord) public promises;

    uint256 public knownUsersCount;
    // list of user ids marked as "known"
    mapping(address => uint256[]) public knownUsersByUserAddr;

    uint256 public userPromiseIdsCount;
    // list of promises created by the user
    mapping(address => uint256[]) public promiseIdsByUserAddr;

    constructor() public {
        name = "PinkyPromise";
    }

    function addUser(string memory _name) public {
        require(msg.sender != address(0));
        usersCount++;
        users[usersCount] = PinkyUserRecord(
            usersCount,
            msg.sender,
            _name,
            false
        );
        userIds[msg.sender] = usersCount;
    }

    function addKnownUser(uint256 _userId, uint256 _knownUserId) public {
        require(_userId != 0 && _userId <= usersCount);
        require(_knownUserId != 0 && _knownUserId <= usersCount);
        require(users[_userId].id != 0);
        require(users[_knownUserId].id != 0);
        require(msg.sender != address(0));

        knownUsersCount++;
        knownUsersByUserAddr[msg.sender].push(_knownUserId);
    }

    function setUserPromiseVisibility(bool _allowOnlyKnownUserPromises) public {
        require(msg.sender != address(0));
        require(users[userIds[msg.sender]].id != 0);

        users[userIds[msg.sender]]
            .allowOnlyKnownUserPromises = _allowOnlyKnownUserPromises;
    }
}
